INSERT INTO `settings` (`settingId`, `label`, `name`, `value`) VALUES
(1, 'Minimum number of objectives', 'SETTING_MIN_OBJ', '4'),
(2, 'Maximum number of objectives', 'SETTING_MAX_OBJ', '5'),
(3, 'Minimum length for input fields', 'SETTING_MIN_CHAR', '1'),
(4, 'Maximum length for input fields', 'SETTING_MAX_CHAR', '1000'),
(5, 'Evaluation Question 1', 'EVALUATION_QUESTION_1', 'How does the individual interact with team members and other colleagues? To what extent does he promote collaboration, communication and conflict handling?'),
(6, 'Evaluation Question 2', 'EVALUATION_QUESTION_2', 'How does the individual communicate with colleagues, peers and subordinates? To what extent does he or she foster a collaborative and open work environment?'),
(7, 'Evaluation Question 3', 'EVALUATION_QUESTION_3', 'To what extent does the individual demonstrate leadership and initiative within the organization? Can you cite situations where he or she has taken proactive steps to solve problems or improve processes?'),
(8, 'Evaluation Question 4', 'EVALUATION_QUESTION_4', 'How does the individual encourage professional and personal development within the team? To what extent does he show an interest in the well-being and growth of his colleagues?'),
(9, 'Evaluation Question 5', 'EVALUATION_QUESTION_5', 'How does the individual communicate team goals and individual roles clearly ?'),
(10, 'Evaluation Question 1 FR', 'EVALUATION_QUESTION_FR_1', 'Comment l\'individu interagit-il avec les membres de l\'quipe et les autres collgues ? Dans quelle mesure favorise-t-il la collaboration, la communication et la gestion des conflits ?'),
(11, 'Evaluation Question 2 FR', 'EVALUATION_QUESTION_FR_2', 'Comment l\'individu communique-t-il avec les collgues, les pairs et les subordonns ? Dans quelle mesure favorise-t-il un environnement de travail collaboratif et ouvert ?'),
(12, 'Evaluation Question 3 FR', 'EVALUATION_QUESTION_FR_3', 'Dans quelle mesure l\'individu fait-il preuve de leadership et d\'initiative au sein de l\'organisation ? Pouvez-vous citer des situations o il ou elle a pris des mesures proactives pour rsoudre des problmes ou amliorer des processus ?'),
(13, 'Evaluation Question 4 FR', 'EVALUATION_QUESTION_FR_4', 'Comment l\'individu encourage-t-il le dveloppement professionnel et personnel au sein de l\'quipe ? Dans quelle mesure montre-t-il un intrt pour le bien-tre et la croissance de ses collgues ?'),
(14, 'Evaluation Question 5 FR', 'EVALUATION_QUESTION_FR_5', 'Comment l\'individu communique-t-il clairement les objectifs de l\'quipe et les rles individuels ?');
